import logo from "./logo.svg";
import style from "./App.module.css";
import { EmailIcon, PasswordIcon, PhoneIcon, UserIcon } from "./Icons/FabIcons";
import { useRef, useState } from "react";

function App() {
  const nameRef = useRef();
  const emailRef = useRef();
  const numRef = useRef();
  const passRef = useRef();
  const genRef = useRef();
  const langRef = useRef();
  const zcodeRef = useRef();
  const aboutRef = useRef();

  const [showContainer, setShowContainer] = useState(true);
  const [name, setName] = useState("");
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [phone, setphone] = useState("");
  const [gender, setgender] = useState("");
  const [language, setlanguage] = useState("");
  const [zcode, setzcode] = useState("");
  const [about, setabout] = useState("");
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(event.target[0]);
    const name = nameRef.current.value;

    if (
      name.trim().length === 0 ||
      emailRef.current.value.trim().length === 0 ||
      numRef.current.value.trim().length === 0 ||
      passRef.current.value.trim().length === 0
    ) {
      alert("Please enter Valid data");
      return;
    }
    const userName = name
      .split(" ")
      .map((item) => item.charAt(0).toUpperCase() + item.slice(1))
      .join(" ");
    setName(userName);
    setShowContainer(false);
  };
  return (
    <div className={style.container}>
      <form onSubmit={handleSubmit} className={style.formContainer}>
        <div className={style.formComp}>
          <label className={style.label}>Name:</label>
          <input ref={nameRef} type="text"></input>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>Email:</label>
          <input ref={emailRef} type="email"></input>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>Password:</label>
          <input ref={passRef} type="text"></input>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>Phone Number:</label>
          <input ref={passRef} type="text"></input>
        </div>

        <div className={style.formComp}>
          <label className={style.label}>Gender:</label>
          <div className={style.radioContainer}>
            <input type="radio" id="male" name="gender" value="Male" />
            <label className={style.label} for="male">
              Male
            </label>
            <input type="radio" id="female" name="gender" value="Male" />
            <label className={style.label} for="female">
              Female
            </label>
            <input type="radio" id="other" name="gender" value="Male" />
            <label className={style.label} for="other">
              Other
            </label>
          </div>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>Language:</label>
          <select className={style.select} name="language" id="language">
            <option value="Select">Select a Language</option>
            <option value="English">English</option>
            <option value="Hindi">Hindi</option>
            <option value="Odia">Odia</option>
          </select>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>Zip Code:</label>
          <input ref={passRef} type="text"></input>
        </div>
        <div className={style.formComp}>
          <label className={style.label}>About Yourself:</label>
          <textarea ref={passRef} type="text"></textarea>
        </div>
        <button type="submit" className={style.button}>
          Register
        </button>
      </form>
    </div>
  );
}

export default App;
